INSERT INTO MODULE (NAME,DESCRIPTION,CREATED_DATE, MODIFIED_DATE, DELETED) VALUES
    ('Tinklapiai','Informacijos išteklis saityne, kuris gali būti pasiektas naudojantis naršykle. Puslapius sujungiant, sudaroma interneto svetainė. Informacija dažniausiai pateikiama HTML[1] arba XHTML, nors šiuo metu tiesiogiai šiomis kalbomis jie rašomi labai retai. Paprastai naudojamos į teksto redaktorius panašios vartotojui draugiškos programos.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Technologijos','Informacinių technologijų modulio studijų dalykai parinkti atsižvelgiant į Informatikos bendrųjų programų projektą (IBPP), akcentuojant skaitmeninį raštingumą, informacinį mąstymą, siekiant ugdyti skaitmeninį intelektą.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Kūno kultūra','Palakstykite vaikai.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Kino kritika','Individuali ar kolektyvinė kino kūrinių analizė. Kino kritikas yra savo srities profesionalas, apžvelgiantis kino filmus ir rašantis recenzijas, skirtas plačiajai visuomenei, paprastai laikraščiams, žurnalams ar specialiai auditorijai. Kino kritikai tiria, analizuoja nacionalinių ir tarptautinių kino filmų kūrimą; analizuoja ir vertina tam tikrus kino filmus, autorius, stilius, mokyklas, tendencijas; pateikia nuomones ir vertinimus žiniasklaidoje.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Joga','Indų filosofijos mokymas, apimantis įvairius fizinius ir mentalinius pratimus kaip asanas, pranajama, meditaciją arba askezę. Jogos teorinis pagrindas – Samkhja, o joga – praktinis išsivadavimo metodas. Kitaip nei samkhja, joga išpažįsta asmenišką aukščiausiąjį Dievą. Joga grindžiama idėja, kad koncentracija, meditacija bei askeze žmogus gali nuraminti dvasią, pasiekti gilesnę įžvalgą ir išsivaduoti iš materialiosios prakriti.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Muzika','Garsų menas, meniškų garsinių artefaktų kūrimas (t. y. garsų ir jų struktūrų improvizavimas, komponavimas, aranžavimas) ir tokių akustinių artefaktų atlikimo (dainavimas, grojimas, dirigavimas) ir jų sąmoningo ar pusiau sąmoningo klausymosi aktai.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Architektūra','Pastatų projektavimo menas ir mokslas. Pagrindiniai architektūros tikslai yra formuoti aplinkos erdvę, projektuoti statinius bei jų kompleksus. Plačiąja šio žodžio reikšme architektūros objektas yra žmogaus gyvenamosios aplinkos planavimas ir organizavimas, pradedant miestų planavimu, landšafto architektūra ir baigiant interjero dizainu.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Pedagogika','Edukologijos dalis – kryptingo ir sistemingo augančiosios kartos ugdymo (auklėjimo) mokslas. Pedagogika yra istoriškai susiformavusi vaikų ir jaunimo rengimo sistema, tikslingai organizuota ir vykdoma ugdytojų ir ugdytinių konstruktyviu bendravimu atsižvelgiant į socialinius tikslus.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Šunų dresūra','Į šuns auklėjimą turi būti įtraukti visi šeimos nariai.Kas beauklėtų šuniuką, visi privalo taikyti tas pačias taisykles. Šuns dresūra - žaidimas + poilsis + darbas.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Kalbos','Domina individualūs kursai, o gal norite suorganizuoti kalbų kursus savo įmonės darbuotojams? Skrivanek komanda mielai pasiūlys Jūsų poreikius atitinkančius mokymus ir padės pagerinti bendrinės bei specializuotos kalbos žinias. Nerandate Jums tinkančio pasiūlymo? Susisiekite su mumis ir padėsime rasti sprendimą Jūsų įmonei. Beje, mokytis galite tiek gyvai, tiek nuotoliniu būdu – tobulėkite iš bet kurio pasaulio krašto.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Vadyba','Empiriškai stebimas organizacijos valdymas bei specialioji (konkrečioji arba praktinė) disciplina, nagrinėjanti organizacijos valdymą ir kontrolę. Tarp tipinių vadybos užduočių yra: strateginis planavimas; ilgalaikių rėminių koncepcijų strateginėms veiklos sritims nustatymas. Veiksmingų sisteminių struktūrų sukūrimas, diegimas ir palaikymas, pvz., planavimo ir kontrolės sistemos, organizacinė struktūra.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Istorija','Mokslo šaka, tirianti ir interpretuojanti visuomenės raidos procesą, žmonijos praeitį. Pasakojimų apie savo praeitį turi ir primityvios, ir civilizuotos žmonių bendrijos. Rašytinės istorijos tradicija Vakarų civilizacijoje prasideda nuo Herodoto (5 a. pr. Kr.). Modernusis istorijos, kaip praeities tyrinėjimo, mokslas susiformavo 18 a. pabaigoje–19 a. pradžioje. Vėlesnių laikų istorikai siekia atkurti žmonių veiklos rezultatus.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Geografija','Graikai yra pirmoji žinoma kultūra, kuri aktyviai domėjosi geografija kaip mokslu ir filosofija, pagrindiniai veikėjai prisidėję prie geografijos plėtros buvo: Talis iš Mileto, Herodotas, Eratostenas, Hiparchas, Aristotelis, Strabonas ir Ptolemėjas. Romėnų sudarinėjami naujai atrastų šalių žemėlapiai papildė geografijos žinias naujais metodais.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Fizika','Gamtos mokslas, tiriantis visas materijos formas:[2] nuo submikroskopinių dalelių, iš kurių sudarytos visos įprastinės medžiagos (dalelių fizika), iki visos materialios Visatos elgesio (kosmologija).Kai kurios fizikos studijuojamos savybės yra bendros visoms materialioms sistemoms, pavyzdžiui, energijos tvermės dėsnis.','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE),
    ('Matematika','Matematikai tyrinėja struktūras, kurios turi atitikmenis kituose tiksliuosiuose moksluose, pavyzdžiui, fizikoje, taip pat apibrėžia naujas struktūras. Pasinaudojant jomis galima rasti ryšius, bendrus analizės metodus tarp labai skirtingų mokslo sričių ir jų tyrimų objektų, palengvinti dažnai atliekamus skaičiavimus. ','2023-02-15 00:36:54','2023-02-16 00:36:54', FALSE);
